import axios, {AxiosError, AxiosResponse} from 'axios'

export type HttpError = AxiosError

export const post = async <T, R>(url: string,
                                 headers: Record<string, string>,
                                 payload: T,
                                 timeout?: number): Promise<AxiosResponse<R>> =>
    axios<T, AxiosResponse<R>>({
        method: 'POST',
        url,
        headers,
        data: payload,
        timeout,
    })